package ch03;

public class A05_Logicaloperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println((10 > 5) && (3 < 5));
		System.out.println((10 > 5) && (3 > 5));
		
		System.out.println((10 > 5) || (3 < 5));
		System.out.println((10 > 5) || (3 > 5));
		
		System.out.println(!(10 > 5));
	}

}
